    private void txtYearMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_txtYearMouseClicked

        
        if(prev!=0)
            txtYear.setText(prev--+"");
        else {
            try{prev=Integer.parseInt(txtYear.getText());
                
            }
            catch(Exception ex)
            {
                ex.printStackTrace();
                JOptionPane.showMessageDialog(null,"please input a valid year");
            }
             
        }
           
        
    }//GEN-LAST:event_txtYearMouseClicked

    private void txtCopyMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_txtCopyMouseClicked
      txtCopy.setText(txt1.getText()+b+"@gmail.com"); 
        
        b++;
    }//GEN-LAST:event_txtCopyMouseClicked

